INSERT INTO game_mode (name,description,is_event,number_of_players) VALUES ('ALL PICK','Игроки могут свободно выбирать любых героев по собственному желанию из всех доступных в игре. Доступен выбор случайного героя. После начала матча также можно обменяться героями с союзниками по команде.',false,10);
INSERT INTO game_mode (name,description,is_event,number_of_players) VALUES ('SINGLE DRAFT','Игроки выбирают из набора, состоящего из одного героя силы, одного героя ловкости, одного героя интеллекта, и одного универсального героя. Для каждого игрока выбор героя уникален.',false,10);